import pandas as pd
from typing import Dict, Any
from .base import BaseIndicator


class MACDIndicator(BaseIndicator):
    def __init__(self, config: Dict[str, Any] | None = None):
        default_config = {
            "fast": 12,
            "slow": 26,
            "signal": 9,
            "signal_strength": 0.25,
        }
        cfg = {**default_config, **(config or {})}
        super().__init__(cfg)
        self._last_signal_series: pd.Series | None = None
        self._last_hist_series: pd.Series | None = None

    @property
    def name(self) -> str:
        return f"MACD{self.config['fast']}_{self.config['slow']}_{self.config['signal']}"

    def validate_config(self) -> None:
        fast = self.config.get("fast")
        slow = self.config.get("slow")
        signal = self.config.get("signal")
        strength = self.config.get("signal_strength")
        if not isinstance(fast, int) or fast < 1:
            raise ValueError("fast must be positive int")
        if not isinstance(slow, int) or slow < 1:
            raise ValueError("slow must be positive int")
        if fast >= slow:
            raise ValueError("fast must be less than slow")
        if not isinstance(signal, int) or signal < 1:
            raise ValueError("signal must be positive int")
        if not 0 <= strength <= 1:
            raise ValueError("signal_strength must be 0..1")

    def calculate(self, data: pd.DataFrame) -> pd.Series:  # type: ignore[name-defined]
        if "close" not in data.columns:
            raise ValueError("Data must contain 'close' column")

        close = data["close"].astype(float)
        ema_fast = close.ewm(span=self.config["fast"], adjust=False).mean()
        ema_slow = close.ewm(span=self.config["slow"], adjust=False).mean()
        macd = ema_fast - ema_slow
        signal_line = macd.ewm(span=self.config["signal"], adjust=False).mean()
        hist = macd - signal_line

        # store for use in generate_signal
        self._last_signal_series = signal_line
        self._last_hist_series = hist

        return macd

    def generate_signal(self, data: pd.DataFrame, indicator_values: pd.Series) -> float:  # type: ignore[name-defined]
        # Ensure we have a freshly calculated signal series
        if self._last_signal_series is None or len(self._last_signal_series) != len(indicator_values):
            # recalc to populate internal series
            _ = self.calculate(data)

        if len(data) == 0 or len(indicator_values) == 0:
            return 0.0

        macd_last = indicator_values.iloc[-1]
        signal_last = self._last_signal_series.iloc[-1] if self._last_signal_series is not None else 0.0

        if pd.isna(macd_last) or pd.isna(signal_last):
            return 0.0

        hist_last = macd_last - signal_last
        # Normalize by a sensible scale to get a value roughly in -1..1
        denom = max(abs(signal_last), abs(macd_last), 1e-8)
        ratio = hist_last / denom

        base = self.config["signal_strength"]
        if ratio > 0:
            return min(1.0, base + ratio * (1.0 - base))
        return max(-1.0, -base + ratio * (1.0 - base))

