#!/usr/bin/env python3
"""
Base abstract class for chat finder implementations.

Defines the common interface that all chat finders must implement.
"""

from __future__ import annotations

import json
import pathlib
from abc import ABC, abstractmethod
from typing import List, Dict, Any, Optional, Literal
from dataclasses import dataclass, field


MessageType = Literal["user", "assistant", "thinking", "tool_use", "tool_result", "code_diff"]


@dataclass
class ChatMessage:
    """Represents a single message or action in a chat session.

    Supports multiple message types:
    - user: User input text
    - assistant: AI text response
    - thinking: AI reasoning/planning
    - tool_use: AI using a tool (Read, Write, Edit, etc.)
    - tool_result: Result from tool execution
    - code_diff: When AI modifies a file
    """
    type: MessageType
    content: str
    metadata: Optional[Dict[str, Any]] = None

    def to_dict(self) -> Dict[str, Any]:
        """Convert to dictionary format."""
        result = {
            "type": self.type,
            "content": self.content
        }
        if self.metadata:
            result["metadata"] = self.metadata
        return result


@dataclass
class ProjectInfo:
    """Represents project/workspace information."""
    name: str
    root_path: str

    def to_dict(self) -> Dict[str, str]:
        return {"name": self.name, "rootPath": self.root_path}


@dataclass
class ChatSession:
    """Represents a complete chat session."""
    project: ProjectInfo
    session_id: str
    messages: List[ChatMessage] = field(default_factory=list)
    date: str = ""
    file_path: str = ""
    workspace_id: str = ""
    raw_data: Optional[Any] = None

    def to_dict(self) -> Dict[str, Any]:
        """Convert to dictionary format matching the existing API."""
        result = {
            "project": self.project.to_dict(),
            "session_id": self.session_id,
            "messages": [msg.to_dict() for msg in self.messages],
            "date": self.date,
        }

        if self.file_path:
            result["file_path"] = self.file_path
        if self.workspace_id:
            result["workspace_id"] = self.workspace_id
        if self.raw_data is not None:
            # Name the raw data field based on the finder type
            # This will be customized by subclasses
            result["raw_data"] = self.raw_data

        return result


class ChatFinder(ABC):
    """Abstract base class for chat history finders.

    All chat finder implementations should inherit from this class
    and implement the required abstract methods.
    """

    def __init__(self):
        """Initialize the chat finder."""
        self._storage_root: Optional[pathlib.Path] = None

    @property
    @abstractmethod
    def name(self) -> str:
        """Return the name of this finder (e.g., 'Copilot', 'Cursor', 'Claude')."""
        pass

    @abstractmethod
    def get_storage_root(self) -> Optional[pathlib.Path]:
        """Return the root directory where chat data is stored.

        Returns:
            Path to the storage directory, or None if not found.
        """
        pass

    @abstractmethod
    def find_chat_sessions(self) -> List[ChatSession]:
        """Find and extract all chat sessions from storage.

        Returns:
            List of ChatSession objects.
        """
        pass

    def extract_chats(self) -> List[Dict[str, Any]]:
        """Main entry point: extract all chats and return as dictionaries.

        Returns:
            List of chat dictionaries matching the existing API format.
        """
        sessions = self.find_chat_sessions()
        return [session.to_dict() for session in sessions]

    def save_to_file(self, output_path: pathlib.Path) -> List[Dict[str, Any]]:
        """Extract chats and save them to a JSON file.

        Args:
            output_path: Path to the output JSON file.

        Returns:
            List of extracted chat dictionaries.
        """
        chats = self.extract_chats()
        output_path.write_text(
            json.dumps(chats, ensure_ascii=False, indent=2),
            encoding="utf-8"
        )
        return chats

    @property
    def storage_root(self) -> Optional[pathlib.Path]:
        """Cached storage root path."""
        if self._storage_root is None:
            self._storage_root = self.get_storage_root()
        return self._storage_root

    def __repr__(self) -> str:
        return f"{self.__class__.__name__}(name={self.name!r}, storage_root={self.storage_root})"


# Helper functions for creating specific message types

def create_user_message(content: str, metadata: Optional[Dict[str, Any]] = None) -> ChatMessage:
    """Create a user message.

    Optionally attach raw metadata to preserve original payloads.
    """
    return ChatMessage(type="user", content=content, metadata=metadata)


def create_assistant_message(content: str, metadata: Optional[Dict[str, Any]] = None) -> ChatMessage:
    """Create an assistant text message.

    Optionally attach raw metadata to preserve original payloads.
    """
    return ChatMessage(type="assistant", content=content, metadata=metadata)


def create_thinking_message(content: str, metadata: Optional[Dict[str, Any]] = None) -> ChatMessage:
    """Create a thinking/reasoning message.

    Optionally attach raw metadata to preserve original payloads.
    """
    return ChatMessage(type="thinking", content=content, metadata=metadata)


def create_tool_use_message(
    tool_name: str,
    tool_input: Dict[str, Any],
    tool_id: Optional[str] = None,
    content: Optional[str] = None,
    metadata: Optional[Dict[str, Any]] = None
) -> ChatMessage:
    """Create a tool use message."""
    base_meta = {
        "tool_name": tool_name,
        "tool_id": tool_id,
        "input": tool_input
    }
    if metadata:
        # Merge given metadata but keep base keys
        base_meta.update(metadata)

    return ChatMessage(
        type="tool_use",
        content=content or f"Using {tool_name}",
        metadata=base_meta
    )


def create_tool_result_message(
    tool_use_id: str,
    result_content: str,
    tool_name: Optional[str] = None,
    metadata: Optional[Dict[str, Any]] = None
) -> ChatMessage:
    """Create a tool result message."""
    base_meta = {"tool_use_id": tool_use_id}
    if tool_name:
        base_meta["tool_name"] = tool_name
    if metadata:
        base_meta.update(metadata)

    return ChatMessage(
        type="tool_result",
        content=result_content,
        metadata=base_meta
    )


def create_code_diff_message(
    file_path: str,
    operation: Literal["write", "edit", "delete"],
    new_content: Optional[str] = None,
    old_content: Optional[str] = None
) -> ChatMessage:
    """Create a code diff message."""
    metadata = {
        "file_path": file_path,
        "operation": operation
    }
    if new_content is not None:
        metadata["new_content"] = new_content
    if old_content is not None:
        metadata["old_content"] = old_content

    content_desc = f"{operation.capitalize()} file: {file_path}"
    return ChatMessage(
        type="code_diff",
        content=content_desc,
        metadata=metadata
    )

#!/usr/bin/env python3
"""
Message parsers for different AI chat systems.

This module contains specialized parsers for extracting detailed message information
from Claude Code, GitHub Copilot, and Cursor chat data.
"""

from __future__ import annotations

import json
import ast
from typing import List, Dict, Any, Optional

from base_chat_finder import (
    ChatMessage,
    create_user_message,
    create_assistant_message,
    create_thinking_message,
    create_tool_use_message,
    create_tool_result_message,
    create_code_diff_message,
)


class ClaudeMessageParser:
    """Parser for Claude Code JSONL transcript messages."""

    @staticmethod
    def parse_transcript_objects(transcript_objects: List[Dict[str, Any]]) -> List[ChatMessage]:
        """Parse Claude Code transcript objects into structured messages.

        Claude stores messages in JSONL format where each object can be:
        - A message with role and content (content can be a list of typed blocks)
        - Each content block can be: text, thinking, tool_use, or tool_result
        """
        messages = []

        for obj in transcript_objects:
            if not isinstance(obj, dict):
                continue

            # Handle nested message structure (Claude JSONL format)
            if "message" in obj and isinstance(obj["message"], dict):
                obj = obj["message"]

            role = obj.get("role")
            content = obj.get("content")

            if not role or not content:
                continue

            # Normalize role
            if role in ["user", "human", "User"]:
                role = "user"
            elif role in ["assistant", "ai", "Assistant", "claude"]:
                role = "assistant"
            else:
                continue

            # Content can be a string, list, or dict
                if isinstance(content, str):
                # Try to parse if it looks like a stringified list
                if content.strip().startswith("["):
                    try:
                        content = ast.literal_eval(content)
                    except (ValueError, SyntaxError):
                        # If parsing fails, treat as regular text
                        if role == "user":
                            messages.append(create_user_message(content, metadata={"raw": obj}))
                        else:
                            messages.append(create_assistant_message(content, metadata={"raw": obj}))
                        continue
                else:
                    # Regular text content
                    if role == "user":
                        messages.append(create_user_message(content))
                    else:
                        messages.append(create_assistant_message(content))
                    continue

            # Content is a list of blocks
            if isinstance(content, list):
                for block in content:
                    if isinstance(block, dict):
                        block_type = block.get("type")

                        if block_type == "text":
                            text_content = block.get("text", "")
                            if text_content:
                                if role == "user":
                                    messages.append(create_user_message(text_content, metadata={"raw": block}))
                                else:
                                    messages.append(create_assistant_message(text_content, metadata={"raw": block}))

                        elif block_type == "thinking":
                            thinking_content = block.get("thinking", "")
                            if thinking_content:
                                messages.append(create_thinking_message(thinking_content, metadata={"raw": block}))

                        elif block_type == "tool_use":
                            tool_name = block.get("name", "unknown")
                            tool_id = block.get("id")
                            tool_input = block.get("input", {})
                            messages.append(create_tool_use_message(
                                tool_name=tool_name,
                                tool_input=tool_input,
                                tool_id=tool_id,
                                metadata={"raw": block}
                            ))

                            # Check if this is a file modification tool
                            if tool_name in ["Write", "Edit"]:
                                file_path = tool_input.get("file_path", "")
                                if file_path:
                                    operation = "write" if tool_name == "Write" else "edit"
                                    new_content = tool_input.get("content") or tool_input.get("new_string")
                                    old_content = tool_input.get("old_string")
                                    messages.append(create_code_diff_message(
                                        file_path=file_path,
                                        operation=operation,
                                        new_content=new_content,
                                        old_content=old_content
                                    ))

                        elif block_type == "tool_result":
                            tool_use_id = block.get("tool_use_id", "")
                            result_content = block.get("content", "")
                            if isinstance(result_content, list):
                                # Join multiple content parts
                                result_parts = []
                                for part in result_content:
                                    if isinstance(part, dict):
                                        result_parts.append(part.get("text", "") or str(part))
                                    else:
                                        result_parts.append(str(part))
                                result_content = "\n".join(result_parts)
                            elif isinstance(result_content, dict):
                                result_content = str(result_content)

                            messages.append(create_tool_result_message(
                                tool_use_id=tool_use_id,
                                result_content=str(result_content),
                                metadata={"raw": block}
                            ))

                    elif isinstance(block, str):
                        # Simple string in the list
                        if role == "user":
                            messages.append(create_user_message(block, metadata={"raw": block}))
                        else:
                            messages.append(create_assistant_message(block, metadata={"raw": block}))

            # Content is a dict
            elif isinstance(content, dict):
                content_str = content.get("text", "") or content.get("content", "") or str(content)
                if role == "user":
                    messages.append(create_user_message(content_str, metadata={"raw": content}))
                else:
                    messages.append(create_assistant_message(content_str, metadata={"raw": content}))

        return messages


class CopilotMessageParser:
    """Parser for GitHub Copilot chat messages."""

    @staticmethod
    def parse_chat_session(copilot_raw: Dict[str, Any]) -> List[ChatMessage]:
        """Parse Copilot chat session from raw data.

        Copilot stores chat data in a `requests` array where each request has:
        - message: The user's input
        - response: Array of response items with `kind` field (thinking, text, tool_use, etc.)
        """
        messages = []

        if not isinstance(copilot_raw, dict):
            return messages

        requests = copilot_raw.get("requests", [])

        for request in requests:
            if not isinstance(request, dict):
                continue

            # Parse user message
            user_message = request.get("message", {})
            if isinstance(user_message, dict):
                user_text = user_message.get("text", "")
                if user_text:
                    messages.append(create_user_message(user_text))

            # Parse response items
            response_items = request.get("response", [])
            if not isinstance(response_items, list):
                continue

            for item in response_items:
                if not isinstance(item, dict):
                    continue

                kind = item.get("kind", "")

                if kind == "thinking":
                    thinking_text = item.get("value", "")
                    if thinking_text:
                        messages.append(create_thinking_message(thinking_text))

                elif kind == "text":
                    text_content = item.get("value", "")
                    if text_content:
                        messages.append(create_assistant_message(text_content))

                elif kind == "markdownContent":
                    markdown = item.get("value", "")
                    if markdown:
                        messages.append(create_assistant_message(markdown))

                elif kind == "toolUse":
                    tool_name = item.get("toolName", "unknown")
                    tool_id = item.get("id")
                    tool_input = item.get("input", {})
                    messages.append(create_tool_use_message(
                        tool_name=tool_name,
                        tool_input=tool_input,
                        tool_id=tool_id
                    ))

                elif kind == "editResponse":
                    # Code edit/diff
                    edits = item.get("edits", [])
                    for edit in edits:
                        if isinstance(edit, dict):
                            file_path = edit.get("filePath", "")
                            if file_path:
                                messages.append(create_code_diff_message(
                                    file_path=file_path,
                                    operation="edit",
                                    new_content=edit.get("newText"),
                                    old_content=edit.get("oldText")
                                ))

                elif kind == "codeBlockResponse":
                    # Code generation response
                    code_content = item.get("value", "")
                    language = item.get("language", "")
                    if code_content:
                        content = f"```{language}\n{code_content}\n```" if language else code_content
                        messages.append(create_assistant_message(content))

        return messages


class CursorMessageParser:
    """Parser for Cursor chat messages."""

    @staticmethod
    def parse_messages(raw_messages: List[Dict[str, str]]) -> List[ChatMessage]:
        """Parse Cursor messages.

        Cursor stores simple role/content messages. We'll try to detect
        tool usage patterns in the content if they exist.
        """
        messages = []

        for msg in raw_messages:
            role = msg.get("role", "")
            content = msg.get("content", "")

            if not role or not content:
                continue

            # Check if content contains tool usage patterns
            # (Cursor might embed this in the text)
            if "```" in content and role == "assistant":
                # This might be a code block - could be a code diff
                # For now, treat as assistant message
                messages.append(create_assistant_message(content))
            elif role == "user":
                messages.append(create_user_message(content))
            elif role == "assistant":
                messages.append(create_assistant_message(content))

        return messages


# Convenience functions for use in finders

def parse_claude_messages(transcript_objects: List[Dict[str, Any]]) -> List[ChatMessage]:
    """Parse Claude Code transcript objects into structured messages."""
    return ClaudeMessageParser.parse_transcript_objects(transcript_objects)


def parse_copilot_messages(copilot_raw: Dict[str, Any]) -> List[ChatMessage]:
    """Parse Copilot chat session from raw data."""
    return CopilotMessageParser.parse_chat_session(copilot_raw)


def parse_cursor_messages(raw_messages: List[Dict[str, str]]) -> List[ChatMessage]:
    """Parse Cursor messages."""
    return CursorMessageParser.parse_messages(raw_messages)

