#!/usr/bin/env python3
"""
Message parsers for different AI chat systems.

This module contains specialized parsers for extracting detailed message information
from Claude Code, GitHub Copilot, and Cursor chat data.
"""

from __future__ import annotations

import json
import ast
from typing import List, Dict, Any, Optional

from base_chat_finder import (
    ChatMessage,
    create_user_message,
    create_assistant_message,
    create_thinking_message,
    create_tool_use_message,
    create_tool_result_message,
    create_code_diff_message,
)


class ClaudeMessageParser:
    """Parser for Claude Code JSONL transcript messages."""

    @staticmethod
    def parse_transcript_objects(transcript_objects: List[Dict[str, Any]]) -> List[ChatMessage]:
        """Parse Claude Code transcript objects into structured messages.

        Claude stores messages in JSONL format where each object can be:
        - A message with role and content (content can be a list of typed blocks)
        - Each content block can be: text, thinking, tool_use, or tool_result
        """
        messages = []

        for obj in transcript_objects:
            if not isinstance(obj, dict):
                continue

            # Handle nested message structure (Claude JSONL format)
            if "message" in obj and isinstance(obj["message"], dict):
                obj = obj["message"]

            role = obj.get("role")
            content = obj.get("content")

            if not role or not content:
                continue

            # Normalize role
            if role in ["user", "human", "User"]:
                role = "user"
            elif role in ["assistant", "ai", "Assistant", "claude"]:
                role = "assistant"
            else:
                continue

            # Content can be a string, list, or dict
                if isinstance(content, str):
                # Try to parse if it looks like a stringified list
                if content.strip().startswith("["):
                    try:
                        content = ast.literal_eval(content)
                    except (ValueError, SyntaxError):
                        # If parsing fails, treat as regular text
                        if role == "user":
                            messages.append(create_user_message(content, metadata={"raw": obj}))
                        else:
                            messages.append(create_assistant_message(content, metadata={"raw": obj}))
                        continue
                else:
                    # Regular text content
                    if role == "user":
                        messages.append(create_user_message(content))
                    else:
                        messages.append(create_assistant_message(content))
                    continue

            # Content is a list of blocks
            if isinstance(content, list):
                for block in content:
                    if isinstance(block, dict):
                        block_type = block.get("type")

                        if block_type == "text":
                            text_content = block.get("text", "")
                            if text_content:
                                if role == "user":
                                    messages.append(create_user_message(text_content, metadata={"raw": block}))
                                else:
                                    messages.append(create_assistant_message(text_content, metadata={"raw": block}))

                        elif block_type == "thinking":
                            thinking_content = block.get("thinking", "")
                            if thinking_content:
                                messages.append(create_thinking_message(thinking_content, metadata={"raw": block}))

                        elif block_type == "tool_use":
                            tool_name = block.get("name", "unknown")
                            tool_id = block.get("id")
                            tool_input = block.get("input", {})
                            messages.append(create_tool_use_message(
                                tool_name=tool_name,
                                tool_input=tool_input,
                                tool_id=tool_id,
                                metadata={"raw": block}
                            ))

                            # Check if this is a file modification tool
                            if tool_name in ["Write", "Edit"]:
                                file_path = tool_input.get("file_path", "")
                                if file_path:
                                    operation = "write" if tool_name == "Write" else "edit"
                                    new_content = tool_input.get("content") or tool_input.get("new_string")
                                    old_content = tool_input.get("old_string")
                                    messages.append(create_code_diff_message(
                                        file_path=file_path,
                                        operation=operation,
                                        new_content=new_content,
                                        old_content=old_content
                                    ))

                        elif block_type == "tool_result":
                            tool_use_id = block.get("tool_use_id", "")
                            result_content = block.get("content", "")
                            if isinstance(result_content, list):
                                # Join multiple content parts
                                result_parts = []
                                for part in result_content:
                                    if isinstance(part, dict):
                                        result_parts.append(part.get("text", "") or str(part))
                                    else:
                                        result_parts.append(str(part))
                                result_content = "\n".join(result_parts)
                            elif isinstance(result_content, dict):
                                result_content = str(result_content)

                            messages.append(create_tool_result_message(
                                tool_use_id=tool_use_id,
                                result_content=str(result_content),
                                metadata={"raw": block}
                            ))

                    elif isinstance(block, str):
                        # Simple string in the list
                        if role == "user":
                            messages.append(create_user_message(block, metadata={"raw": block}))
                        else:
                            messages.append(create_assistant_message(block, metadata={"raw": block}))

            # Content is a dict
            elif isinstance(content, dict):
                content_str = content.get("text", "") or content.get("content", "") or str(content)
                if role == "user":
                    messages.append(create_user_message(content_str, metadata={"raw": content}))
                else:
                    messages.append(create_assistant_message(content_str, metadata={"raw": content}))

        return messages


class CopilotMessageParser:
    """Parser for GitHub Copilot chat messages."""

    @staticmethod
    def parse_chat_session(copilot_raw: Dict[str, Any]) -> List[ChatMessage]:
        """Parse Copilot chat session from raw data.

        Copilot stores chat data in a `requests` array where each request has:
        - message: The user's input
        - response: Array of response items with `kind` field (thinking, text, tool_use, etc.)
        """
        messages = []

        if not isinstance(copilot_raw, dict):
            return messages

        requests = copilot_raw.get("requests", [])

        for request in requests:
            if not isinstance(request, dict):
                continue

            # Parse user message
            user_message = request.get("message", {})
            if isinstance(user_message, dict):
                user_text = user_message.get("text", "")
                if user_text:
                    messages.append(create_user_message(user_text, metadata={"raw": user_message}))

            # Parse response items
            response_items = request.get("response", [])
            if not isinstance(response_items, list):
                continue

            for item in response_items:
                if not isinstance(item, dict):
                    continue

                kind = item.get("kind", "")

                if kind == "thinking":
                    thinking_text = item.get("value", "")
                    if thinking_text:
                        messages.append(create_thinking_message(thinking_text, metadata={"raw": item}))

                elif kind == "text":
                    text_content = item.get("value", "")
                    if text_content:
                        messages.append(create_assistant_message(text_content, metadata={"raw": item}))

                elif kind == "markdownContent":
                    markdown = item.get("value", "")
                    if markdown:
                        messages.append(create_assistant_message(markdown, metadata={"raw": item}))

                elif kind == "toolUse":
                    tool_name = item.get("toolName", "unknown")
                    tool_id = item.get("id")
                    tool_input = item.get("input", {})
                    messages.append(create_tool_use_message(
                        tool_name=tool_name,
                        tool_input=tool_input,
                        tool_id=tool_id,
                        metadata={"raw": item}
                    ))

                elif kind == "editResponse":
                    # Code edit/diff
                    edits = item.get("edits", [])
                    for edit in edits:
                        if isinstance(edit, dict):
                            file_path = edit.get("filePath", "")
                            if file_path:
                                messages.append(create_code_diff_message(
                                    file_path=file_path,
                                    operation="edit",
                                    new_content=edit.get("newText"),
                                    old_content=edit.get("oldText")
                                ))

                elif kind == "codeBlockResponse":
                    # Code generation response
                    code_content = item.get("value", "")
                    language = item.get("language", "")
                        if code_content:
                        content = f"```{language}\n{code_content}\n```" if language else code_content
                        messages.append(create_assistant_message(content, metadata={"raw": item}))

        return messages


class CursorMessageParser:
    """Parser for Cursor chat messages."""

    @staticmethod
    def parse_messages(raw_messages: List[Dict[str, str]]) -> List[ChatMessage]:
        """Parse Cursor messages.

        Cursor stores simple role/content messages. We'll try to detect
        tool usage patterns in the content if they exist.
        """
        messages = []

        for msg in raw_messages:
            role = msg.get("role", "")
            content = msg.get("content", "")

            if not role or not content:
                continue

            # Check if content contains tool usage patterns
            # (Cursor might embed this in the text)
            if "```" in content and role == "assistant":
                # This might be a code block - could be a code diff
                # For now, treat as assistant message
                messages.append(create_assistant_message(content, metadata={"raw": msg}))
            elif role == "user":
                messages.append(create_user_message(content, metadata={"raw": msg}))
            elif role == "assistant":
                messages.append(create_assistant_message(content, metadata={"raw": msg}))

        return messages


# Convenience functions for use in finders

def parse_claude_messages(transcript_objects: List[Dict[str, Any]]) -> List[ChatMessage]:
    """Parse Claude Code transcript objects into structured messages."""
    return ClaudeMessageParser.parse_transcript_objects(transcript_objects)


def parse_copilot_messages(copilot_raw: Dict[str, Any]) -> List[ChatMessage]:
    """Parse Copilot chat session from raw data."""
    return CopilotMessageParser.parse_chat_session(copilot_raw)


def parse_cursor_messages(raw_messages: List[Dict[str, str]]) -> List[ChatMessage]:
    """Parse Cursor messages."""
    return CursorMessageParser.parse_messages(raw_messages)

#!/usr/bin/env python3
"""
Find and extract all Cursor chat histories from a user's system.

This script locates all workspace and session databases and extracts chat data
from Cursor's SQLite storage.
"""

from __future__ import annotations

import json
import pathlib
import platform
import datetime
import sqlite3
import logging
import os
from typing import List, Optional, Dict, Any, Iterable, Tuple
from collections import defaultdict

from base_chat_finder import ChatFinder, ChatSession, ChatMessage, ProjectInfo
from chat_message_parser import parse_cursor_messages

# Configure logging
logging.basicConfig(level=logging.INFO)
logger = logging.getLogger(__name__)


class CursorChatFinder(ChatFinder):
    """Find and extract Cursor chat histories from SQLite databases."""

    @property
    def name(self) -> str:
        return "Cursor"

    def get_storage_root(self) -> Optional[pathlib.Path]:
        """Return the path to Cursor's storage directory."""
        home = pathlib.Path.home()
        system = platform.system()

        if system == "Darwin":
            return home / "Library" / "Application Support" / "Cursor"
        elif system == "Windows":
            return home / "AppData" / "Roaming" / "Cursor"
        elif system == "Linux":
            return home / ".config" / "Cursor"
        else:
            raise RuntimeError(f"Unsupported OS: {system}")

    def _load_json_from_db(self, cur: sqlite3.Cursor, table: str, key: str) -> Optional[Any]:
        """Load and parse JSON value from a database table."""
        cur.execute(f"SELECT value FROM {table} WHERE key=?", (key,))
        row = cur.fetchone()
        if row:
            try:
                return json.loads(row[0])
            except Exception as e:
                logger.debug(f"Failed to parse JSON for {key}: {e}")
        return None

    def _iter_bubbles_from_disk_kv(self, db: pathlib.Path) -> Iterable[Tuple[str, str, str, str]]:
        """Yield (composerId, role, text, db_path) from cursorDiskKV table."""
        try:
            con = sqlite3.connect(f"file:{db}?mode=ro", uri=True)
            cur = con.cursor()
            # Check if table exists
            cur.execute("SELECT name FROM sqlite_master WHERE type='table' AND name='cursorDiskKV'")
            if not cur.fetchone():
                con.close()
                return

            cur.execute("SELECT key, value FROM cursorDiskKV WHERE key LIKE 'bubbleId:%'")
        except sqlite3.DatabaseError as e:
            logger.debug(f"Database error with {db}: {e}")
            return

        db_path_str = str(db)

        for k, v in cur.fetchall():
            try:
                if v is None:
                    continue

                b = json.loads(v)
            except Exception as e:
                logger.debug(f"Failed to parse bubble JSON for key {k}: {e}")
                continue

            txt = (b.get("text") or b.get("richText") or "").strip()
            if not txt:
                continue
            role = "user" if b.get("type") == 1 else "assistant"
            composerId = k.split(":")[1]  # Format is bubbleId:composerId:bubbleId
            yield composerId, role, txt, db_path_str

        con.close()

    def _iter_composer_data(self, db: pathlib.Path) -> Iterable[Tuple[str, dict, str]]:
        """Yield (composerId, composerData, db_path) from cursorDiskKV table."""
        try:
            con = sqlite3.connect(f"file:{db}?mode=ro", uri=True)
            cur = con.cursor()
            # Check if table exists
            cur.execute("SELECT name FROM sqlite_master WHERE type='table' AND name='cursorDiskKV'")
            if not cur.fetchone():
                con.close()
                return

            cur.execute("SELECT key, value FROM cursorDiskKV WHERE key LIKE 'composerData:%'")
        except sqlite3.DatabaseError as e:
            logger.debug(f"Database error with {db}: {e}")
            return

        db_path_str = str(db)

        for k, v in cur.fetchall():
            try:
                if v is None:
                    continue

                composer_data = json.loads(v)
                composer_id = k.split(":")[1]
                yield composer_id, composer_data, db_path_str

            except Exception as e:
                logger.debug(f"Failed to parse composer data for key {k}: {e}")
                continue

        con.close()

    def _extract_project_info(self, db: pathlib.Path) -> ProjectInfo:
        """Extract project information from workspace database."""
        try:
            con = sqlite3.connect(f"file:{db}?mode=ro", uri=True)
            cur = con.cursor()

            # Get file paths from history entries
            ents = self._load_json_from_db(cur, "ItemTable", "history.entries") or []

            # Extract file paths from history entries
            paths = []
            for e in ents:
                resource = e.get("editor", {}).get("resource", "")
                if resource and resource.startswith("file:///"):
                    paths.append(resource[len("file:///"):])

            # If we found file paths, extract the project name
            if paths:
                common_prefix = os.path.commonprefix(paths)
                last_separator_index = common_prefix.rfind('/')
                if last_separator_index > 0:
                    project_root = common_prefix[:last_separator_index]
                    project_name = pathlib.Path(project_root).name
                    con.close()
                    return ProjectInfo(name=project_name, root_path="/" + project_root.lstrip('/'))

            con.close()
        except Exception as e:
            logger.debug(f"Error extracting project info from {db}: {e}")

        return ProjectInfo(name="(unknown)", root_path="(unknown)")

    def _iter_workspaces(self) -> Iterable[Tuple[str, pathlib.Path]]:
        """Yield (workspace_id, db_path) for each workspace."""
        base = self.storage_root
        if not base:
            return

        ws_root = base / "User" / "workspaceStorage"
        if not ws_root.exists():
            return

        for folder in ws_root.iterdir():
            db = folder / "state.vscdb"
            if db.exists():
                yield folder.name, db

    def _get_global_storage_path(self) -> Optional[pathlib.Path]:
        """Return path to the global storage state.vscdb."""
        base = self.storage_root
        if not base:
            return None

        global_db = base / "User" / "globalStorage" / "state.vscdb"
        if global_db.exists():
            return global_db

        # Legacy paths
        g_dirs = [
            base / "User" / "globalStorage" / "cursor.cursor",
            base / "User" / "globalStorage" / "cursor"
        ]
        for d in g_dirs:
            if d.exists():
                for file in d.glob("*.sqlite"):
                    return file

        return None

    def find_chat_sessions(self) -> List[ChatSession]:
        """Find and extract all Cursor chat sessions."""
        # Maps to track workspaces, composers, and sessions
        ws_proj: Dict[str, ProjectInfo] = {}
        comp_meta: Dict[str, Dict[str, Any]] = {}
        comp2ws: Dict[str, str] = {}
        sessions: Dict[str, Dict[str, Any]] = defaultdict(lambda: {"messages": []})

        # 1. Process workspace DBs first
        logger.debug("Processing workspace databases...")
        for ws_id, db in self._iter_workspaces():
            logger.debug(f"Processing workspace {ws_id} - {db}")
            ws_proj[ws_id] = self._extract_project_info(db)

            # Extract composer metadata
            try:
                con = sqlite3.connect(f"file:{db}?mode=ro", uri=True)
                cur = con.cursor()
                cd = self._load_json_from_db(cur, "ItemTable", "composer.composerData") or {}
                for c in cd.get("allComposers", []):
                    cid = c["composerId"]
                    comp_meta[cid] = {
                        "title": c.get("name", "(untitled)"),
                        "createdAt": c.get("createdAt"),
                        "lastUpdatedAt": c.get("lastUpdatedAt")
                    }
                    comp2ws[cid] = ws_id

                # Extract messages from composer data in ItemTable
                for comp in cd.get("allComposers", []):
                    cid = comp.get("composerId")
                    messages = comp.get("messages", [])
                    for msg in messages:
                        role = msg.get("role", "")
                        content = msg.get("content", "")
                        if role and content:
                            sessions[cid]["messages"].append({"role": role, "content": content})
                            if "db_path" not in sessions[cid]:
                                sessions[cid]["db_path"] = str(db)

                con.close()
            except Exception as e:
                logger.debug(f"Error processing workspace {ws_id}: {e}")

        # 2. Process global storage
        global_db = self._get_global_storage_path()
        if global_db:
            logger.debug(f"Processing global storage: {global_db}")

            # Extract bubbles from cursorDiskKV
            for cid, role, text, db_path in self._iter_bubbles_from_disk_kv(global_db):
                sessions[cid]["messages"].append({"role": role, "content": text})
                if "db_path" not in sessions[cid]:
                    sessions[cid]["db_path"] = db_path
                if cid not in comp_meta:
                    comp_meta[cid] = {"title": f"Chat {cid[:8]}", "createdAt": None, "lastUpdatedAt": None}
                    comp2ws[cid] = "(global)"

            # Extract composer data
            for cid, data, db_path in self._iter_composer_data(global_db):
                if cid not in comp_meta:
                    created_at = data.get("createdAt")
                    comp_meta[cid] = {
                        "title": f"Chat {cid[:8]}",
                        "createdAt": created_at,
                        "lastUpdatedAt": created_at
                    }
                    comp2ws[cid] = "(global)"

                if "db_path" not in sessions[cid]:
                    sessions[cid]["db_path"] = db_path

                # Extract conversation from composer data
                conversation = data.get("conversation", [])
                for msg in conversation:
                    msg_type = msg.get("type")
                    if msg_type is None:
                        continue

                    # Type 1 = user, Type 2 = assistant
                    role = "user" if msg_type == 1 else "assistant"
                    content = msg.get("text", "")
                    if content and isinstance(content, str):
                        sessions[cid]["messages"].append({"role": role, "content": content})

        # 3. Build final list of ChatSession objects
        results: List[ChatSession] = []
        for cid, data in sessions.items():
            if not data["messages"]:
                continue

            ws_id = comp2ws.get(cid, "(unknown)")
            project = ws_proj.get(ws_id, ProjectInfo(name="(unknown)", root_path="(unknown)"))
            meta = comp_meta.get(cid, {"title": "(untitled)", "createdAt": None, "lastUpdatedAt": None})

            # Convert messages to ChatMessage objects using the parser
            messages = parse_cursor_messages(data["messages"])

            # Format date
            created_at = meta.get("createdAt")
            if created_at and isinstance(created_at, (int, float)):
                date = datetime.datetime.fromtimestamp(created_at / 1000).strftime("%Y-%m-%d %H:%M:%S")
            else:
                date = datetime.datetime.now().strftime("%Y-%m-%d %H:%M:%S")

            session = ChatSession(
                project=project,
                session_id=cid,
                messages=messages,
                date=date,
                file_path=data.get("db_path", ""),
                workspace_id=ws_id,
                raw_data=data
            )

            results.append(session)

        # Sort by last updated time if available
        results.sort(
            key=lambda s: comp_meta.get(s.session_id, {}).get("lastUpdatedAt") or 0,
            reverse=True
        )

        return results


def extract_all_chats() -> List[Dict[str, Any]]:
    """Legacy function for backwards compatibility."""
    finder = CursorChatFinder()
    return finder.extract_chats()


def save_all_chats(output_path: pathlib.Path) -> List[Dict[str, Any]]:
    """Legacy function for backwards compatibility."""
    finder = CursorChatFinder()
    return finder.save_to_file(output_path)


if __name__ == "__main__":
    import argparse

    parser = argparse.ArgumentParser(description="Extract all Cursor chat histories")
    parser.add_argument(
        "--out",
        type=pathlib.Path,
        default=pathlib.Path("cursor_chats.json"),
        help="Output JSON file (default: cursor_chats.json)"
    )
    args = parser.parse_args()

    finder = CursorChatFinder()
    chats = finder.save_to_file(args.out)
    print(f"Extracted {len(chats)} chat sessions to {args.out}")

#!/usr/bin/env python3
"""
Small CLI to parse provided chat export files with the project's parsers and save the parsed
chat sessions (while preserving the original raw data) into JSON files.

Example:
  python tools/parse_and_save.py --in claude_chats.json --out-dir ./parsed
  python tools/parse_and_save.py --in some_workspace/chatSessions/*.json --out-dir ./parsed
"""

from __future__ import annotations

import json
import argparse
import pathlib
import datetime
from typing import Any, List

from base_chat_finder import ProjectInfo, ChatSession
from chat_message_parser import (
    parse_claude_messages,
    parse_copilot_messages,
    parse_cursor_messages,
)


def _read_json(path: pathlib.Path) -> Any:
    try:
        return json.loads(path.read_text(encoding="utf-8"))
    except Exception:
        return None


def _read_jsonl(path: pathlib.Path) -> List[dict]:
    objs = []
    try:
        with path.open("r", encoding="utf-8") as f:
            for ln in f:
                ln = ln.strip()
                if not ln:
                    continue
                try:
                    objs.append(json.loads(ln))
                except Exception:
                    continue
    except Exception:
        pass
    return objs


def parse_and_save_file(path: pathlib.Path, out_dir: pathlib.Path):
    basename = path.stem
    project = ProjectInfo(name="(local)", root_path=str(path.parent))
    date = datetime.datetime.fromtimestamp(path.stat().st_mtime).strftime("%Y-%m-%d %H:%M:%S")

    if path.suffix.lower() == ".jsonl":
        raw = _read_jsonl(path)
        if not raw:
            print(f"No valid JSONL objects in {path}")
            return
        messages = parse_claude_messages(raw)
        session = ChatSession(project=project, session_id=basename, messages=messages, date=date, file_path=str(path), raw_data=raw)

    else:
        raw = _read_json(path)
        if raw is None:
            print(f"Failed to read JSON file: {path}")
            return

        # Heuristics for type detection
        messages = []
        if isinstance(raw, dict) and "requests" in raw:
            # Copilot
            messages = parse_copilot_messages(raw)
            session = ChatSession(project=project, session_id=basename, messages=messages, date=date, file_path=str(path), raw_data=raw)
        elif isinstance(raw, dict) and ("transcript" in raw or "project" in raw or "claude" in path.name.lower()):
            # Claude-style JSON or simple transcript
            # Some Claude exports are JSON objects or arrays
            if isinstance(raw, dict) and "transcript" in raw and isinstance(raw.get("transcript"), list):
                raw_list = raw.get("transcript")
            elif isinstance(raw, list):
                raw_list = raw
            else:
                raw_list = [raw]

            messages = parse_claude_messages(raw_list)
            session = ChatSession(project=project, session_id=basename, messages=messages, date=date, file_path=str(path), raw_data=raw)
        elif isinstance(raw, dict) and ("messages" in raw or (isinstance(raw, list) and raw and isinstance(raw[0], dict) and "role" in raw[0])):
            # Cursor-style or simple messages
            # Accept either a dict with messages or a plain list of messages
            raw_messages = raw.get("messages") if isinstance(raw, dict) and raw.get("messages") else raw
            messages = parse_cursor_messages(raw_messages)
            session = ChatSession(project=project, session_id=basename, messages=messages, date=date, file_path=str(path), raw_data=raw)
        else:
            print(f"Unable to detect chat format for {path}; skipping.")
            return

    out_path = out_dir / f"{basename}_parsed.json"
    out_dir.mkdir(parents=True, exist_ok=True)
